mapper.select:
select *
from users
where user_id = #{userId} 

and pw = #{pw}

select count(user_id)
from users
where user_id = #{userId}

select user_id
from users
where phone = #{phone} and user_name = #{userName}

select count(nickname)
from users
where nickname = #{nickname}

mapper.update:
update users
set has_admin_rights = #{hasAdminRights}
where user_id = #{userId}

update users
set is_suspended = #{isSuspended}
where user_id = #{userId}

update users
set user_name = #{userName}
where user_id = #{userId}

update users
set nickname = #{nickname}, phone = #{phone}, email = #{email}, address = #{address}, datailed_address = #{detailedAddress},
birthdate = #{birthdate}, user_intro = #{userIntro}, user_profile_image_filename = #{userProfileImageFilename}
where user_id = #{userId}
